package operterinjava;

public class LogicalOpertor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20,c=30;
		boolean d=(a<b)&&(b>c);
		System.out.println("LOGICAL AND OPERATION: "+d);
		boolean e=(a<b)||(b>c);
		System.out.println("LOGICAL OR OPERATION: "+e);
	}

}
