package operterinjava;

public class RelationalOperter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a,b;
        a=20;
        b=30;
        boolean z;
        z=a<b;
        System.out.println("a less than b: "+z);
        z=a>b;
        System.out.println("a greter than b: "+z);
        z=a<=b;
        System.out.println("a less equal than b: "+z);
        z=a<=b;
        System.out.println("a greter equal than b: "+z);
        z=a==b;
        System.out.println("a equal b: "+z);
        z=a!=b;
        System.out.println("a not equal b: "+z);
	}

}
