package operterinjava;

public class TernaryOpertor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=40,b=40;
		int x=(a>b)?100:200;
		System.out.println("value of x: "+x);
		int y=(a==b)?100:200;
		System.out.println("value of y: "+y);

	}

}
